import React, {useEffect, useState} from "react" ;
import { getTask } from "../api/taskApi";
import "./home.css";

function Home (){
    const [tasks, setTasks]= useState([]);
    const user_id ='6eab0ea6-5aaa-4437-a8f8-b59c74ff6293';

    useEffect(() => {
    getTask(user_id)
    .then(res => setTasks(res.data.tasks))
    .catch(err => console.log(err));
},[]);

    return(
        <div className="container mt-4">
            <form className="mb-4" onSubmit={dd}>
                <input name='title' placeholder="Enter Title" className="form-control mb-2" value='' onChange={hh}></input>                
                <input name='description' placeholder="Enter Description" className="form-control mb-2" value='' onChange={hh}></input>
                <select name='status' className="form-control mb-2"></select>
            </form>
            <h2 className="text-center mb-4">Your Tasks</h2>
            <ul className="list-group">
                {tasks.map(task => (
                <li
                    key={task.id}
                    className={`list-group-item d-flex justify-content-between align-items-center ${
                    task.status === 'completed' ? 'text-muted text-decoration-line-through' : ''
                    }`}
                >
                    <span>{task.title}</span>
                    <span className="badge bg-primary">{task.status}</span>
                </li>
                ))}
            </ul>
        </div>
    );
};

export default Home;
